document.addEventListener("DOMContentLoaded",function(e){document.body;const t=document.querySelector(".header__container"),a=document.querySelector(".header__menu"),s=document.querySelector(".header__btn");function c(){t.classList.contains("active")?(t.classList.remove("active"),a.style.maxHeight=0):(t.classList.add("active"),a.style.maxHeight=a.scrollHeight+"px",a.style.opacity=1)}s.addEventListener("click",()=>{requestAnimationFrame(c)});const n=window.matchMedia("(max-width: 1300px)");n.addEventListener("change",function(e){e.matches?(a.style.maxHeight=0,a.style.opacity=0):(t.classList.remove("active"),a.style.maxHeight=a.scrollHeight+"px",a.style.opacity=1)});const l=document.querySelector(".panel__showCategories");l.addEventListener("click",()=>{requestAnimationFrame(()=>doCategoriesMenuToggle(l))})});const panel_search=document.getElementById("panel-search"),clear_panel_search_btn=document.getElementById("panel-search-clear");function doCategoriesMenuToggle(e){e.classList.toggle("active"),console.log("asdf")}panel_search.addEventListener("input",e=>{Boolean(e.target.value)?(panel_search.classList.add("active"),clear_panel_search_btn.classList.add("visible")):(panel_search.classList.remove("active"),clear_panel_search_btn.classList.remove("visible"))}),clear_panel_search_btn.addEventListener("click",()=>{panel_search.value="",panel_search.classList.remove("active"),panel_search.focus(),clear_panel_search_btn.classList.remove("visible")});